import React, { useEffect, useState } from 'react';
import { getAllPrincipals } from '../services/principalService';
import '../css/sidebar.css';
import '../css/student.css';

const Principal = () => {
  const [principals, setPrincipals] = useState([]);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
      name: '',
      college: '',
      password: '',
      permission: '',
      email: '',
      address: '',
      colgid: '',
    });

     useEffect(() => {
        fetchStudents();
      }, []);
    
      const fetchStudents = () => {
        getAllStudents()
          .then(response => {
            setStudents(response.data);
            setFilteredStudents(response.data); // initially show all
          })
          .catch(err => {
            setError('Failed to fetch students');
            console.error(err);
          });
      };


      const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };


  useEffect(() => {
    getAllPrincipals()
      .then(response => {
        setPrincipals(response.data);
      })
      .catch(err => {
        setError('Failed to fetch principals');
        console.error(err);
      });
  }, []);


  const handleSubmit = (e) => {
      e.preventDefault();
      createStudent(formData)
        .then(() => {
          setFormData({
            name: '',
            email: '',
            password: '',
            permission: '',
            rollnumber: '',
            address: '',
            colgid: '',
          });
          fetchStudents();
        })
        .catch(err => {
          setError('Failed to add student');
          console.error(err);
        });
    };

  return (
    <div className="box">
      <h1>Principal Data</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <table className="student-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Password</th>
            <th>College ID</th>
            <th>College</th>
            <th>Permission</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {principals.map(principal => (
            <tr key={principal.id}>
            <td>{principal.name}</td>
            <td>{principal.email}</td>
            <td>{principal.password}</td>
            <td>{principal.colgid}</td>
            <td>{principal.college.colgname}</td>
            <td>{principal.permission}</td>
            <td>
                <button>Edit</button>
                <button>Delete</button>
            </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Principal;
