import React, { useEffect, useState } from 'react';
import { getAllAdmins, createAdmin } from '../services/adminService'; 
import '../css/sidebar.css';
import '../css/student.css';

const Admin = () => {
  const [admins, setAdmins] = useState([]);           // Holds all admin data
  const [error, setError] = useState('');             // Holds error message
  const [formData, setFormData] = useState({          // Holds form input values
    name: '',
    email: '',
    password: '',
    permission: ''
  });

  useEffect(() => {
    fetchAdmins();                                    // Load admins on page load
  }, []);

  const fetchAdmins = () => {
    getAllAdmins()
      .then(response => {
        setAdmins(response.data);                     // Set admins into state
      })
      .catch(err => {
        setError('Failed to fetch admins');
        console.error(err);
      });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value }); // Update form field state
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createAdmin(formData)                                // Call API to add admin
      .then(() => {
        setFormData({ name: '', email: '', password: '', permission: '' }); // Clear form
        fetchAdmins();                                // Refresh admin list
      })
      .catch(err => {
        setError('Failed to add admin');
        console.error(err);
      });
  };

  return (
    <div className="box">
      <h1>Admin Data</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Add Admin Form */}
      <form onSubmit={handleSubmit} className="admin-form">
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
        <input type="text" name="permission" placeholder="Permission" value={formData.permission} onChange={handleChange} required />
        <button type="submit">Add Admin</button>
      </form>

      {/* Admin Table */}
      <table className="student-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Password</th>
            <th>Permission</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {admins.map(admin => (
            <tr key={admin.id}>
              <td>{admin.name}</td>
              <td>{admin.email}</td>
              <td>{admin.password}</td>
              <td>{admin.permission}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Admin;
